<template>
  <div @click="openWallet"
       class="relative magic-card  border-l-4 border-l-blue-500 border border-gray-200  hover:shadow-md hover:cursor-pointer  overflow-hidden p-4  bg-white  rounded-xl shadow-sm  "
       :id="props.wallet.id"
  >


    <div class="  h-full left-0 ">
      <!-- Header -->
      <div class="flex justify-between gap-x-3">
        <div class="flex  gap-x-3">
          <!-- Icon -->
          <div class="mb-3 inline-flex justify-center items-center size-8 md:size-10   text-gray-700 ">

            <img class="inline-block h-4rem] w-[5rem] shadow rounded-full"
                 src="/icons/bitcoin.svg"
                 alt="Image Description"
            />

          </div>
          <!-- End Icon -->

          <div class="flex flex-col">

            <div class="text-gray-800 font-semibold text-xl">
              {{ props.wallet.name }}
            </div>

            <div class="flex items-center space-x-0.5">
              <div class="text-xs">P2WSH -</div>
              <div class="text-center  text-gray-800 text-xs"><b>2</b> of 3</div>
            </div>
          </div>

          <!--              <pre>-->
          <!--                {{wallet}}-->
          <!--              </pre>-->

        </div>

        <div>

          <div class="flex items-center -space-x-2">
             <span v-for="(user) in props.wallet.users"
                   :key="user.id"
                   class="flex flex-shrink-0 justify-center items-center size-8 bg-gray-200 border-2 border-white text-gray-700 text-xs font-medium uppercase rounded-full dark:bg-neutral-700 dark:border-neutral-800 dark:text-neutral-300">
            {{ getUserInitials(user) }}
          </span>
          </div>
          <div class="text-center mt-0.5 text-gray-800 text-xs"><b>{{ usersCount }}</b>
            {{ usersCount > 1 ? 'users' : 'user' }}
          </div>

        </div>
      </div>
      <!-- End Header -->

      <hr class="opacity-75 mt-2">

      <div class="flex w-full mt-2 items-end justify-between">

        <div class="text-base font-light text-gray-800 ">
          Balance:
        </div>

        <BtcSatsValue :satoshi="props.wallet.balance.receivedBalance" v-slot="{value,currency}">
          <div class="flex items-end space-x-2">
            <h3 class="text-xl font-semibold  text-gray-800 ">
              {{ value }}
            </h3>
            <span>
                 <span class="text-xs text-gray-500">{{ currency }}</span>
            </span>
          </div>
        </BtcSatsValue>

      </div>


    </div>

  </div>
</template>

<script setup>
import {useRouter} from "vue-router"
import ROUTES_NAMES from "@/router/RouteNames";

const props = defineProps({
  wallet: {
    type: Object,
    required: true
  }
})

const router = useRouter()

const usersCount = props.wallet.users.length

const getUserInitials = (user) => {
  const firstName = user?.firstName || ''
  const lasName = user?.lastName || ''

  let userInitials = ''
  if (firstName !== '' && lasName !== '') {
    userInitials = `${firstName.charAt(0)}${lasName.charAt(0)}`
  } else {
    userInitials = `${user.email.charAt(0)}${user.email.charAt(user.email.indexOf('@') + 1)}`
  }
  return userInitials
}

const openWallet = () => router.push({
  name: ROUTES_NAMES.WALLETS.VIEW_WALLET,
  params: {
    id: props.wallet.id
  }
})

</script>


